import React from "react";
import { css, cx } from "emotion";
// import Client from "fortnite";
import "./App.css";

// const fortnite = new Client("425c6cf9-c137-4149-977f-377b7bd33f06");

const team = [
  { name: "iamfabriceg", uid: "2b7897075f20421b81529bdc2ab742f3" },
  { name: "SKIDIP lukyvj", uid: "ff29ca89e591485e8d450c2aec558c15" },
  { name: "SKIPID AntShelby", uid: "c605a87eba634e8dbfc7fba4448fa87d" },
  { name: "SKIDIP Kingom", uid: "0d4f175a990f44a08e0447b56ed42703" }
];

const Board = props => {
  return (
    <div
      className={css`
        height: 100vh;
      `}
    >
      <h1>@{props.name}</h1>
      hello
      <div>
        {props.wins}
        {props.kills}

        {props.kd}

        {props.matchesplayed}
        {props.winrate}
      </div>
    </div>
  );
};
function App() {
  return (
    <div
      className={cx(
        "App",
        css`
          display: grid;
          grid-template-columns: repeat(4, 1fr);
          grid-gap: 10px;
          grid-auto-rows: minmax(100px, auto);
        `
      )}
    >
      {/*eslint-disable-next-line array-callback-return*/}
      {team.map((member, index) => {
        let statApi =
          "https://fortnite-public-api.theapinetwork.com/prod09/users/public/br_stats?user_id=" +
          member.uid +
          "&platform=ps4";

        // httpRequestAsync(statApi, theResponse);

        fetch(statApi)
          .then(response => response.json())
          .then(data => {
            console.log(data);
            return (
              <Board
                key={index}
                uid={member.uid}
                plateform={data.plateform}
                name={data.username}
                wins={data.totals.wins}
                kills={data.totals.kills}
                kd={data.totals.kd}
                matchesplayed={data.totals.matchesplayed}
                winrate={data.totals.winrate + "%"}
              />
            );
          });
      })}
    </div>
  );
}

export default App;
