import React from "react";
import { css, cx } from "emotion";
// import Client from "fortnite";
import "./App.css";

// const fortnite = new Client("425c6cf9-c137-4149-977f-377b7bd33f06");

const team = [
  { name: "iamfabriceg", uid: "2b7897075f20421b81529bdc2ab742f3" },
  { name: "SKIDIP lukyvj", uid: "ff29ca89e591485e8d450c2aec558c15" },
  { name: "SKIPID AntShelby", uid: "c605a87eba634e8dbfc7fba4448fa87d" },
  { name: "SKIDIP Kingom", uid: "0d4f175a990f44a08e0447b56ed42703" }
];

const Board = props => {
  let statApi =
    "https://fortnite-public-api.theapinetwork.com/prod09/users/public/br_stats?user_id=" +
    props.uid +
    "&platform=ps4";

  httpRequestAsync(statApi, theResponse);
  let name, wins, kills, kd, matches, winrate;
  /*  getting response from API and defining variables   */
  function theResponse(response) {
    console.log("getting stats of " + props.uid);
    let jsonObject = JSON.parse(response);
    //example of getting data from api
    console.log("Name: " + jsonObject.username);
    console.log("Wins: " + jsonObject.totals.wins);
    console.log("Kills: " + jsonObject.totals.kills);
    console.log("K/D: " + jsonObject.totals.kd);
    console.log("Matches: " + jsonObject.totals.matchesplayed);
    console.log("Winrate: " + jsonObject.totals.winrate + "%");
    name = jsonObject.username;
    wins = jsonObject.totals.wins;
    kills = jsonObject.totals.kills;
    kd = jsonObject.totals.kd;
    matches = jsonObject.totals.matchesplayed;
    winrate = jsonObject.totals.winrate;
  }

  return (
    <div
      className={css`
        height: 100vh;
      `}
    >
      <h1>@{props.name}</h1>
      <div>
        Name: {name}
        Wins: {wins}
        Kills: {kills}
        K/D:{kd}
        Matches: {matches}
        Winrate: {winrate}%
      </div>
    </div>
  );
};
function App() {
  return (
    <div
      className={cx(
        "App",
        css`
          display: grid;
          grid-template-columns: repeat(4, 1fr);
          grid-gap: 10px;
          grid-auto-rows: minmax(100px, auto);
        `
      )}
    >
      {team.map((member, index) => (
        <Board name={member.name} uid={member.uid} key={index} />
      ))}
    </div>
  );
}

function httpRequestAsync(api, callback) {
  var httpRequest = new XMLHttpRequest();
  httpRequest.onreadystatechange = () => {
    if (httpRequest.readyState === 4 && httpRequest.status === 200)
      callback(httpRequest.responseText);
  };
  httpRequest.open("GET", api, true);
  httpRequest.send();
}

export default App;
