import React from "react";
import { css, cx } from "emotion";
// import Client from "fortnite";
import "./App.css";

// const fortnite = new Client("425c6cf9-c137-4149-977f-377b7bd33f06");

const team = [
  { name: "iamfabriceg", uid: "2b7897075f20421b81529bdc2ab742f3" },
  { name: "SKIDIP lukyvj", uid: "ff29ca89e591485e8d450c2aec558c15" },
  { name: "SKIPID AntShelby", uid: "c605a87eba634e8dbfc7fba4448fa87d" },
  { name: "SKIDIP Kingom", uid: "0d4f175a990f44a08e0447b56ed42703" }
];

const Board = props => (
  <div
    className={css`
      height: 100vh;
    `}
  >
    <h1>@{props.name}</h1>
    <div>
      {props.wins}
      {props.kills}

      {props.kd}

      {props.matchesplayed}
      {props.winrate}
    </div>
  </div>
);
class Leaderboard extends React.Component {
  render() {
    return this.props.data.map((member, index) => (
      <Board
        key={index}
        uid={member.uid}
        plateform={member.plateform}
        name={member.username}
        wins={member.totals.wins}
        kills={member.totals.kills}
        kd={member.totals.kd}
        matchesplayed={member.totals.matchesplayed}
        winrate={member.totals.winrate + "%"}
      />
    ));
  }
}
class App extends React.Component {
  componentDidMount() {
    let teamData = [];
    team.forEach((member, index) => {
      let statApi =
        "https://fortnite-public-api.theapinetwork.com/prod09/users/public/br_stats?user_id=" +
        member.uid +
        "&platform=ps4";

      fetch(statApi)
        .then(response => response.json())
        .then(data => {
          teamData.push(data);
        });
    });
    this.setState({ data: teamData });
  }
  render() {
    return (
      <div
        className={cx(
          "App",
          css`
            display: grid;
            grid-template-columns: repeat(4, 1fr);
            grid-gap: 10px;
            grid-auto-rows: minmax(100px, auto);
          `
        )}
      >
        <h1>SKIDIP</h1>

        <Leaderboard data={this.state.data} />
      </div>
    );
  }
}

export default App;
